export const BrandMarquee = () => {
  const { width } = useWindowSize()
  const lenis = useStore(({ lenis }) => lenis)
  const wrapperRef = useRef(null)
  const [mousePosition, setMousePosition] = useState({ x: 0, y: 0 })
  const [mounted, setMounted] = useState(false)

  // Set mounted state on client-side
  useEffect(() => {
    setMounted(true)
  }, [])

  // For parallax effect with scroll - with layoutEffect: false to prevent hydration issues
  const { scrollYProgress } = useScroll({
    target: wrapperRef,
    offset: ['start end', 'end start'],
    layoutEffect: false, // Prevent hydration warnings
  })

  const y1 = useTransform(scrollYProgress, [0, 1], [0, -50])
  const y2 = useTransform(scrollYProgress, [0, 1], [0, 50])

  // For mouse parallax effect
  const handleMouseMove = (e) => {
    if (!wrapperRef.current) return

    const { clientX, clientY } = e
    const { left, top, width, height } =
      wrapperRef.current.getBoundingClientRect()
    const x = (clientX - left) / width - 0.5
    const y = (clientY - top) / height - 0.5

    setMousePosition({ x, y })
  }

  // For mouse leave reset
  const handleMouseLeave = () => {
    setMousePosition({ x: 0, y: 0 })
  }

  // Custom variants for logo animation
  const logoVariants = {
    hidden: {
      opacity: 0.5,
      scale: 0.9,
    },
    visible: {
      opacity: 1,
      scale: 1,
      transition: {
        type: 'spring',
        stiffness: 100,
        damping: 15,
      },
    },
    hover: {
      opacity: 1,
      scale: 1.15,
      transition: {
        type: 'spring',
        stiffness: 400,
        damping: 20,
      },
    },
  }

  // Fixed animation duration to avoid hydration mismatch
  const duration = 45

  // Calculate mouse-based transformations
  const mouseX = useMotionValue(0)
  const mouseY = useMotionValue(0)

  useEffect(() => {
    mouseX.set(mousePosition.x * -15)
    mouseY.set(mousePosition.y * -15)
  }, [mousePosition, mouseX, mouseY])

  // Only render the full component after client-side mounting to prevent hydration issues
  return (
    <motion.div
      className={s.wrapper}
      ref={wrapperRef}
      onMouseMove={handleMouseMove}
      onMouseLeave={handleMouseLeave}
      initial={{ opacity: 0, y: 30 }}
      animate={{ opacity: 1, y: 0 }}
      transition={{ duration: 0.6, ease: [0.16, 1, 0.3, 1] }}
    >
      <motion.div className={s.marquee} style={{ y: mounted ? y1 : 0 }}>
        <motion.div
          className={s.marqueeGroup}
          animate={{ x: 0 }}
          style={{
            animationDuration: `${duration}s`,
          }}
        >
          {brandLogos.slice(0, 8).map((logo) => (
            <motion.div
              key={logo.id}
              className={`${s.logoItem} logoItem`}
              initial="hidden"
              animate="visible"
              whileHover="hover"
              variants={logoVariants}
              style={{
                translateX: mouseX,
                translateY: mouseY,
                rotateX: mousePosition.y * 10,
                rotateY: mousePosition.x * -10,
              }}
            >
              <svg viewBox="0 0 24 24">
                <motion.path
                  d={logo.path}
                  initial={{ pathLength: 0, fillOpacity: 0 }}
                  animate={{ pathLength: 1, fillOpacity: 1 }}
                  transition={{ duration: 1, delay: 0.2, ease: 'easeInOut' }}
                />
              </svg>
            </motion.div>
          ))}
        </motion.div>
        <motion.div
          className={s.marqueeGroup}
          aria-hidden="true"
          animate={{ x: 0 }}
          style={{
            animationDuration: `${duration}s`,
          }}
        >
          {brandLogos.slice(0, 8).map((logo) => (
            <motion.div
              key={`copy-${logo.id}`}
              className={`${s.logoItem} logoItem`}
              initial="hidden"
              animate="visible"
              whileHover="hover"
              variants={logoVariants}
              style={{
                translateX: mouseX,
                translateY: mouseY,
                rotateX: mousePosition.y * 10,
                rotateY: mousePosition.x * -10,
              }}
            >
              <svg viewBox="0 0 24 24">
                <motion.path
                  d={logo.path}
                  initial={{ pathLength: 0, fillOpacity: 0 }}
                  animate={{ pathLength: 1, fillOpacity: 1 }}
                  transition={{ duration: 1, delay: 0.3, ease: 'easeInOut' }}
                />
              </svg>
            </motion.div>
          ))}
        </motion.div>
      </motion.div>

      <motion.div
        className={`${s.marquee} ${s.marqueeReverse}`}
        style={{ y: mounted ? y2 : 0 }}
      >
        <motion.div
          className={s.marqueeGroup}
          animate={{ x: 0 }}
          style={{
            animationDuration: `${duration}s`,
          }}
        >
          {brandLogos.slice(8).map((logo) => (
            <motion.div
              key={logo.id}
              className={`${s.logoItem} logoItem`}
              initial="hidden"
              animate="visible"
              whileHover="hover"
              variants={logoVariants}
              style={{
                translateX: mouseX,
                translateY: mouseY,
                rotateX: mousePosition.y * 10,
                rotateY: mousePosition.x * -10,
              }}
            >
              <svg viewBox="0 0 24 24">
                <motion.path
                  d={logo.path}
                  initial={{ pathLength: 0, fillOpacity: 0 }}
                  animate={{ pathLength: 1, fillOpacity: 1 }}
                  transition={{ duration: 1, delay: 0.4, ease: 'easeInOut' }}
                />
              </svg>
            </motion.div>
          ))}
        </motion.div>
        <motion.div
          className={s.marqueeGroup}
          aria-hidden="true"
          animate={{ x: 0 }}
          style={{
            animationDuration: `${duration}s`,
          }}
        >
          {brandLogos.slice(8).map((logo) => (
            <motion.div
              key={`copy-${logo.id}`}
              className={`${s.logoItem} logoItem`}
              initial="hidden"
              animate="visible"
              whileHover="hover"
              variants={logoVariants}
              style={{
                translateX: mouseX,
                translateY: mouseY,
                rotateX: mousePosition.y * 10,
                rotateY: mousePosition.x * -10,
              }}
            >
              <svg viewBox="0 0 24 24">
                <motion.path
                  d={logo.path}
                  initial={{ pathLength: 0, fillOpacity: 0 }}
                  animate={{ pathLength: 1, fillOpacity: 1 }}
                  transition={{ duration: 1, delay: 0.5, ease: 'easeInOut' }}
                />
              </svg>
            </motion.div>
          ))}
        </motion.div>
      </motion.div>
    </motion.div>
  )
}
